Property.Value:
BEGIN
--quick win IF ([metadata].[GetPropertyValueInternal]('ExecutionPrecursorProc')) <> '[dbo].[DemoModePrecursor]' BEGIN EXEC [metadataHelpers].[AddProperty] @PropertyName = N'ExecutionPrecursorProc', @PropertyValue = N'[dbo].[DemoModePrecursor]'; END;
--reduce wait times ;WITH cte AS ( SELECT [PipelineId], LEFT(ABS(CAST(CAST(NEWID() AS VARBINARY(192)) AS INT)),1) AS NewValue FROM [metadata].[PipelineParameters] ) UPDATE pp SET pp.[ParameterValue] = cte.[NewValue] FROM [metadata].[PipelineParameters] pp INNER JOIN cte ON pp.[PipelineId] = cte.[PipelineId] INNER JOIN [metadata].[Pipelines] p ON pp.[PipelineId] = p.[PipelineId] WHERE pp.[ParameterName] LIKE 'Wait%' AND p.[Enabled] = 1;
--for intentional error IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentExecution] ) BEGIN UPDATE pp SET pp.[ParameterValue] = 'true' FROM [metadata].[PipelineParameters] pp INNER JOIN [metadata].[Pipelines] p ON pp.[PipelineId] = p.[PipelineId] WHERE p.[PipelineName] = 'Intentional Error' AND pp.[ParameterName] = 'RaiseErrors'; END; ELSE BEGIN UPDATE pp SET pp.[ParameterValue] = 'false' FROM [metadata].[PipelineParameters] pp INNER JOIN [metadata].[Pipelines] p ON pp.[PipelineId] = p.[PipelineId] WHERE p.[PipelineName] = 'Intentional Error' AND pp.[ParameterName] = 'RaiseErrors'; END;
--dependency chain failure handling IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) <> 'DependencyChain' BEGIN EXEC [metadataHelpers].[AddProperty] @PropertyName = N'FailureHandling', @PropertyValue = N'DependencyChain'; END;
--short infant iterations IF ([metadata].[GetPropertyValueInternal]('PipelineStatusCheckDuration')) <> '5' BEGIN EXEC [metadataHelpers].[AddProperty] @PropertyName = N'PipelineStatusCheckDuration', @PropertyValue = N'5'; END; END;

BEGIN
	IF(@RaiseError = 'true')
	BEGIN
		RAISERROR('The Stored Procedure intentionally failed.',16,1);
		RETURN 0;
	END
END;

BEGIN SET NOCOUNT ON;
DECLARE @RestartStatus BIT
--get restart overide property SELECT @RestartStatus = [metadata].[GetPropertyValueInternal]('OverideRestart')
--check for running batch execution IF EXISTS ( SELECT 1 FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Running' ) BEGIN SELECT @LocalExecutionId = [ExecutionId] FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId;
--should never actually be called as handled within Orchestrator pipelines using the Pipeline Run Check utility RAISERROR('There is already an batch execution run in progress. Stop the related parent pipeline via the Orchestrator first.',16,1); RETURN 0; END ELSE IF EXISTS ( SELECT 1 FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Stopped' ) AND @RestartStatus = 0 BEGIN SELECT @LocalExecutionId = [ExecutionId] FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Stopped';
RETURN 0; END ELSE IF EXISTS ( SELECT 1 FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Stopped' ) AND @RestartStatus = 1 BEGIN --clean up current execution table and abandon batch SELECT @LocalExecutionId = [ExecutionId] FROM [metadata].[BatchExecution] WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Stopped';
EXEC [metadata].[UpdateExecutionLog] @PerformErrorCheck = 0, --Special case when OverideRestart = 1; @ExecutionId = @LocalExecutionId;
--abandon previous batch execution UPDATE [metadata].[BatchExecution] SET [BatchStatus] = 'Abandoned' WHERE [BatchId] = @BatchId AND ISNULL([BatchStatus],'') = 'Stopped';
SET @LocalExecutionId = NEWID();
--create new batch run record INSERT INTO [metadata].[BatchExecution] ( [BatchId], [ExecutionId], [BatchName], [BatchStatus], [StartDateTime] ) SELECT [BatchId], @LocalExecutionId, [BatchName], 'Running', GETUTCDATE() FROM [metadata].[Batches] WHERE [BatchId] = @BatchId; END ELSE BEGIN SET @LocalExecutionId = NEWID();
--create new batch run record INSERT INTO [metadata].[BatchExecution] ( [BatchId], [ExecutionId], [BatchName], [BatchStatus], [StartDateTime] ) SELECT [BatchId], @LocalExecutionId, [BatchName], 'Running', GETUTCDATE() FROM [metadata].[Batches] WHERE [BatchId] = @BatchId; END; END;

CREATE PROCEDURE [metadata].[BatchWrapper]
	(
	@BatchId UNIQUEIDENTIFIER,
	@LocalExecutionId UNIQUEIDENTIFIER OUTPUT
	)
AS

BEGIN
	SET NOCOUNT ON;

	-- If any pipelines still have a status of running, mark as failed to block downstream processing, and add an error log
	IF EXISTS
		(
		SELECT 
			*
		FROM 
			[metadata].[CurrentExecution]
		WHERE 
			[LocalExecutionId] = @ExecutionId
			AND [StageId] < @StageId
			AND [PipelineStatus] = 'Running'
		)
		BEGIN		
			DECLARE @RunningPipelineId INT;
			DECLARE @RunningPipelineStageId INT;
			DECLARE @RunId UNIQUEIDENTIFIER;
			DECLARE @ErrorJson NVARCHAR(MAX);
			DECLARE @RunningCursor CURSOR ;

			SET @RunningCursor = CURSOR FOR 
									        SELECT 
										        [PipelineId],
										        [StageId],
										        [PipelineRunId]
									        FROM 
										        [metadata].[CurrentExecution]
									        WHERE 
										        [LocalExecutionId] = @ExecutionId
										        AND [StageId] < @StageId
										        AND [PipelineStatus] = 'Running'

			OPEN @RunningCursor
			FETCH NEXT FROM @RunningCursor INTO @RunningPipelineId, @RunningPipelineStageId, @RunId

			WHILE @@FETCH_STATUS = 0
			BEGIN 
				EXEC [metadata].SetLogPipelineFailed
					@ExecutionId = @ExecutionId,
					@StageId = @RunningPipelineStageId,
					@PipelineId = @RunningPipelineId,
					@RunId = @RunId;

				SET @ErrorJson = '{ "RunId": "' + Cast(@RunId AS CHAR(36)) + '", "Errors": [ { "ActivityRunId": "00000000-0000-0000-0000-000000000000", "ActivityName": "Set Pipeline Result", "ActivityType": "Switch", "ErrorCode": "Unknown", "ErrorType": "Framework Error", "ErrorMessage": "Framework pipeline ''04-Infant'' failed to set the pipeline result, most likely due to a timeout or azure connectivity issue. Check the framework Data Factory monitor for more information." } ] }'
				EXEC metadata.SetErrorLogDetails @LocalExecutionId = @ExecutionId,
                                        @JsonErrorDetails = @ErrorJson;

				FETCH NEXT FROM @RunningCursor INTO @RunningPipelineId, @RunningPipelineStageId, @RunId;
			END;
			CLOSE @RunningCursor;
			DEALLOCATE @RunningCursor;
		END;


	IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'None'
		BEGIN
			--do nothing allow processing to carry on regardless
			RETURN 0;
		END;

	ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'Simple'
		BEGIN
			IF EXISTS
				(
				SELECT 
					*
				FROM 
					[metadata].[CurrentExecution]
				WHERE 
					[LocalExecutionId] = @ExecutionId
					AND [StageId] = @StageId
					AND [IsBlocked] = 1
				)
				BEGIN		
					UPDATE
						[metadata].[BatchExecution]
					SET
						[EndDateTime] = GETUTCDATE(),
						[BatchStatus] = 'Stopped'
					WHERE
						[ExecutionId] = @ExecutionId;

					--Saves the infant pipeline and activities being called throwing the exception at this level.
					RAISERROR('All pipelines are blocked. Stopping processing.',16,1); 
					--If not thrown here, the proc [metadata].[UpdateExecutionLog] would eventually throw an exception.
					RETURN 0;
				END			
		END;

	ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'DependencyChain'
		BEGIN
			IF EXISTS
				(
				SELECT 
					*
				FROM 
					[metadata].[CurrentExecution]
				WHERE 
					[LocalExecutionId] = @ExecutionId
					AND [StageId] = @StageId
					AND [IsBlocked] = 1
				)
				BEGIN		
					DECLARE @PipelineId INT;
					DECLARE @Cursor CURSOR ;

					SET @Cursor = CURSOR FOR 
											SELECT 
												[PipelineId] 
											FROM 
												[metadata].[CurrentExecution]
											WHERE 
												[LocalExecutionId] = @ExecutionId
												AND [StageId] = @StageId 
												AND [IsBlocked] = 1

					OPEN @Cursor
					FETCH NEXT FROM @Cursor INTO @PipelineId

					WHILE @@FETCH_STATUS = 0
					BEGIN 
						EXEC [metadata].[SetExecutionBlockDependants]
							@ExecutionId = @ExecutionId,
							@PipelineId = @PipelineId;

						FETCH NEXT FROM @Cursor INTO @PipelineId;
					END;
					CLOSE @Cursor;
					DEALLOCATE @Cursor;
				END;
		END;
	ELSE
		BEGIN
			RAISERROR('Unknown failure handling state.',16,1);
			RETURN 0;
		END;
END;

BEGIN SET NOCOUNT ON;
DECLARE @SendAlerts BIT DECLARE @AlertingEnabled BIT
--get property SELECT @AlertingEnabled = [metadata].[GetPropertyValueInternal]('UseFrameworkEmailAlerting');
--based on global property IF (@AlertingEnabled = 1) BEGIN --based on piplines to recipients link IF EXISTS ( SELECT pal.AlertId FROM metadata.CurrentExecution AS ce INNER JOIN metadata.AlertOutcomes AS ao ON ao.PipelineOutcomeStatus = ce.PipelineStatus INNER JOIN metadata.PipelineAlertLink AS pal ON pal.PipelineId = ce.PipelineId INNER JOIN metadata.Recipients AS r ON r.RecipientId = pal.RecipientId WHERE ce.PipelineId = @PipelineId AND ( ao.BitValue & pal.OutcomesBitValue <> 0 OR pal.OutcomesBitValue & 1 <> 0 --all ) AND pal.[Enabled] = 1 AND r.[Enabled] = 1 ) BEGIN SET @SendAlerts = 1; END; ELSE BEGIN SET @SendAlerts = 0; END; END ELSE BEGIN SET @SendAlerts = 0; END;
SELECT @SendAlerts AS SendAlerts END;

BEGIN SET NOCOUNT ON;
/* Check 1 - Are there execution stages enabled in the metadata? Check 2 - Are there pipelines enabled in the metadata? Check 3 - Are there any service principals available to run the processing pipelines? Check 4 - Is there at least one TenantId available? Check 5 - Is there at least one SubscriptionId available? Check 6 - Is there a current OverideRestart property available? Check 7 - Are there any enabled pipelines configured without a service principal? Check 8 - Are any Orchestrators set to use the default subscription value? Check 9 - Are any Subscription set to use the default tenant value? Check 10 - Is there a current PipelineStatusCheckDuration property available? Check 11 - Is there a current UseFrameworkEmailAlerting property available? Check 12 - Is there a current EmailAlertBodyTemplate property available? Check 13 - Does the total size of the request body for the pipeline parameters added exceed the Azure Functions size limit when the Worker execute pipeline body is created? Check 14 - Is there a current FailureHandling property available? Check 15 - Does the FailureHandling property have a valid value? Check 16 - When using DependencyChain failure handling, are there any dependants in the same execution stage of the predecessor? Check 17 - Does the SPNHandlingMethod property have a valid value? Check 18 - Does the Service Principal table contain both types of SPN handling for a single credential? Check 19 - Is there a current UseExecutionBatches property available? Check 20 - Is there a current FrameworkFactoryResourceGroup property available? Check 21 - Is there a current PreviousPipelineRunsQueryRange property available?
--Batch execution checks: Check 22 - If using batch executions, is the requested batch name enabled? Check 23 - If using batch executions, does the requested batch have links to execution stages? Check 24 - Have batch executions been enabled after a none batch execution run?
Check 25 - Has the execution failed due to an invalid pipeline name? If so, attend to update this before the next run. Check 26 - Is there more than one framework orchestrator set? Check 27 - Has a framework orchestrator been set for any orchestrators? */
DECLARE @BatchId UNIQUEIDENTIFIER DECLARE @ErrorDetails VARCHAR(500) DECLARE @MetadataIntegrityIssues TABLE ( [CheckNumber] INT NOT NULL, [IssuesFound] VARCHAR(MAX) NOT NULL )
/* Checks: */
--Check 1: IF NOT EXISTS ( SELECT 1 FROM [metadata].[Stages] WHERE [Enabled] = 1 ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 1, 'No execution stages are enabled within the metadatabase. Orchestrator has nothing to run.' ) END;
--Check 2: IF NOT EXISTS ( SELECT 1 FROM [metadata].[Pipelines] WHERE [Enabled] = 1 ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 2, 'No execution pipelines are enabled within the metadatabase. Orchestrator has nothing to run.' ) END;
--Check 3: IF NOT EXISTS ( SELECT 1 FROM [dbo].[ServicePrincipals] ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 3, 'No service principal details have been added to the metadata. Orchestrator cannot authorise pipeline executions.' ) END;
--Check 4: IF NOT EXISTS ( SELECT * FROM [metadata].[Tenants] ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 4, 'TenantId value is missing from the [metadata].[Tenants] table.' ) END;
--Check 5: IF NOT EXISTS ( SELECT * FROM [metadata].[Subscriptions] ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 5, 'SubscriptionId value is missing from the [metadata].[Subscriptions] table.' ) END;
--Check 6: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'OverideRestart' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 6, 'A current OverideRestart value is missing from the properties table.' ) END;
--Check 7: IF EXISTS ( SELECT * FROM [metadata].[Pipelines] p LEFT OUTER JOIN [metadata].[PipelineAuthLink] al ON p.[PipelineId] = al.[PipelineId] WHERE p.[Enabled] = 1 AND al.[PipelineId] IS NULL ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 7, 'Enabled pipelines are missing a valid Service Principal link.' ) END;
--Check 8: IF EXISTS ( SELECT * FROM [metadata].[Orchestrators] WHERE [SubscriptionId] = '12345678-1234-1234-1234-012345678910' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 8, 'Orchestrator still set to use the default subscription value of 12345678-1234-1234-1234-012345678910.' ) END;
--Check 9: IF EXISTS ( SELECT * FROM [metadata].[Subscriptions] WHERE [TenantId] = '12345678-1234-1234-1234-012345678910' AND [SubscriptionId] <> '12345678-1234-1234-1234-012345678910' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 9, 'None default subscription still set to use the default tenant value of 12345678-1234-1234-1234-012345678910.' ) END;
--Check 10: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'PipelineStatusCheckDuration' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 10, 'A current PipelineStatusCheckDuration value is missing from the properties table.' ) END;
--Check 11: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'UseFrameworkEmailAlerting' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 11, 'A current UseFrameworkEmailAlerting value is missing from the properties table.' ) END;
--Check 12: IF ( SELECT [PropertyValue] FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'UseFrameworkEmailAlerting' ) = 1 BEGIN IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'EmailAlertBodyTemplate' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 12, 'A current EmailAlertBodyTemplate value is missing from the properties table.' ) END; END;
--Check 13: IF EXISTS ( SELECT * FROM [metadata].[PipelineParameterDataSizes] WHERE [Size] > 9 /* Azure Function request limit is 10MB. https://docs.microsoft.com/en-us/azure/azure-functions/functions-scale 9MB to allow for other content in execute pipeline body request. */ ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 13, 'The pipeline parameters entered exceed the Azure Function request body maximum of 10MB. Query view [metadata].[PipelineParameterDataSizes] for details.' ) END;
--Check 14: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'FailureHandling' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 14, 'A current FailureHandling value is missing from the properties table.' ) END;
--Check 15: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'FailureHandling' AND [PropertyValue] IN ('None','Simple','DependencyChain') ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 15, 'The property FailureHandling does not have a supported value.' ) END;
--Check 16: IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'DependencyChain' BEGIN IF EXISTS ( SELECT pd.[DependencyId] FROM [metadata].[PipelineDependencies] pd INNER JOIN [metadata].[Pipelines] pp ON pd.[PipelineId] = pp.[PipelineId] INNER JOIN [metadata].[Pipelines] dp ON pd.[DependantPipelineId] = dp.[PipelineId] WHERE pp.[StageId] = dp.[StageId] ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 16, 'A dependant pipeline and its upstream predecessor exist in the same execution stage. Fix this dependency chain to allow correct failure handling.' ) END; END;
--Check 17: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'SPNHandlingMethod' AND [PropertyValue] IN ('StoreInDatabase','StoreInKeyVault') ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 17, 'The property SPNHandlingMethod does not have a supported value.' ) END;
--Check 18: IF EXISTS ( SELECT * FROM [dbo].[ServicePrincipals] WHERE ( [PrincipalId] IS NOT NULL OR [PrincipalSecret] IS NOT NULL ) AND ( [PrincipalIdUrl] IS NOT NULL OR [PrincipalSecretUrl] IS NOT NULL ) ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 18, 'The table [dbo].[ServicePrincipals] can only have one method of SPN details sorted per credential ID.' ) END;
--Check 19: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'UseExecutionBatches' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 19, 'A current UseExecutionBatches value is missing from the properties table.' ) END;
--Check 20: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'FrameworkFactoryResourceGroup' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 20, 'A current FrameworkFactoryResourceGroup value is missing from the properties table.' ) END;
--Check 21: IF NOT EXISTS ( SELECT * FROM [metadata].[CurrentProperties] WHERE [PropertyName] = 'PreviousPipelineRunsQueryRange' ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 21, 'A current PreviousPipelineRunsQueryRange value is missing from the properties table.' ) END;
--batch execution checks IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN IF @BatchName IS NULL BEGIN RAISERROR('A NULL batch name cannot be passed when the UseExecutionBatches property is set to 1 (true).',16,1); RETURN 0; END
SELECT @BatchId = [BatchId] FROM [metadata].[Batches] WHERE [BatchName] = @BatchName;
--Check 22: IF EXISTS ( SELECT 1 FROM [metadata].[Batches] WHERE [BatchId] = @BatchId AND [Enabled] = 0 ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 22, 'The requested execution batch is currently disabled. Enable the batch before proceeding.' ) END;
--Check 23: IF NOT EXISTS ( SELECT 1 FROM [metadata].[BatchStageLink] WHERE [BatchId] = @BatchId ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 23, 'The requested execution batch does not have any linked execution stages. See table [metadata].[BatchStageLink] for details.' ) END;
--Check 24: IF EXISTS ( SELECT * FROM [metadata].[CurrentExecution] c LEFT OUTER JOIN [metadata].[BatchExecution] b ON c.[LocalExecutionId] = b.[ExecutionId] WHERE b.[ExecutionId] IS NULL ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 24, 'Execution records exist in the [metadata].[CurrentExecution] table that do not have a record in [metadata].[BatchExecution] table. Has batch excutions been enabed after an incomplete none batch run?' ) END; END; --end batch checks
--Check 25: IF EXISTS ( SELECT 1 FROM [metadata].[CurrentExecution] WHERE [PipelineStatus] = 'InvalidPipelineNameError' ) BEGIN UPDATE ce SET ce.[PipelineName] = p.[PipelineName] FROM [metadata].[CurrentExecution] ce INNER JOIN [metadata].[Pipelines] p ON ce.[PipelineId] = p.[PipelineId] AND ce.[StageId] = p.[StageId] WHERE ce.[PipelineStatus] = 'InvalidPipelineNameError' END;
--Check 26: IF (SELECT COUNT(0) FROM [metadata].[Orchestrators] WHERE [IsFrameworkOrchestrator] = 1) > 1 BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 26, 'There is more than one FrameworkOrchestrator set in the table [metadata].[Orchestrators]. Only one is supported.' ) END
--Check 27: IF NOT EXISTS ( SELECT 1 FROM [metadata].[Orchestrators] WHERE [IsFrameworkOrchestrator] = 1 ) BEGIN INSERT INTO @MetadataIntegrityIssues VALUES ( 27, 'A FrameworkOrchestrator has not been set in the table [metadata].[Orchestrators]. Only one is supported.' ) END
/* Integrity Checks Outcome: */
--throw runtime error if checks fail IF EXISTS ( SELECT * FROM @MetadataIntegrityIssues ) AND @DebugMode = 0 BEGIN SET @ErrorDetails = 'Metadata integrity checks failed. Run EXEC [metadata].[CheckMetadataIntegrity] @DebugMode = 1; for details.'
RAISERROR(@ErrorDetails, 16, 1); RETURN 0; END;
--report issues when in debug mode IF @DebugMode = 1 BEGIN IF NOT EXISTS ( SELECT * FROM @MetadataIntegrityIssues ) BEGIN PRINT 'No data integrity issues found in metadata.' RETURN 0; END ELSE BEGIN SELECT * FROM @MetadataIntegrityIssues; END; END; END;

CREATE PROCEDURE [metadata].[CheckMetadataIntegrity]
	(
	@DebugMode BIT = 0,
	@BatchName VARCHAR(255) = NULL
	)
AS

BEGIN SET NOCOUNT ON;
DECLARE @BatchId UNIQUEIDENTIFIER;
IF([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '0' BEGIN SET @LocalExecutionId = NEWID();
TRUNCATE TABLE [metadata].[CurrentExecution];
--defensive check IF NOT EXISTS ( SELECT 1 FROM [metadata].[Pipelines] p INNER JOIN [metadata].[Stages] s ON p.[StageId] = s.[StageId] INNER JOIN [metadata].[Orchestrators] d ON p.[OrchestratorId] = d.[OrchestratorId] WHERE p.[Enabled] = 1 AND s.[Enabled] = 1 ) BEGIN RAISERROR('Requested execution run does not contain any enabled stages/pipelines.',16,1); RETURN 0; END;
INSERT INTO [metadata].[CurrentExecution] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName] ) SELECT @LocalExecutionId, p.[StageId], p.[PipelineId], @CallingOrchestratorName, d.[ResourceGroupName], d.[OrchestratorType], d.[OrchestratorName], p.[PipelineName] FROM [metadata].[Pipelines] p INNER JOIN [metadata].[Stages] s ON p.[StageId] = s.[StageId] INNER JOIN [metadata].[Orchestrators] d ON p.[OrchestratorId] = d.[OrchestratorId] WHERE p.[Enabled] = 1 AND s.[Enabled] = 1;
SELECT @LocalExecutionId AS ExecutionId; END ELSE IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN DELETE FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @LocalExecutionId;
SELECT @BatchId = [BatchId] FROM [metadata].[BatchExecution] WHERE [ExecutionId] = @LocalExecutionId;
--defensive check IF NOT EXISTS ( SELECT 1 FROM [metadata].[Pipelines] p INNER JOIN [metadata].[Stages] s ON p.[StageId] = s.[StageId] INNER JOIN [metadata].[Orchestrators] d ON p.[OrchestratorId] = d.[OrchestratorId] INNER JOIN [metadata].[BatchStageLink] b ON b.[StageId] = s.[StageId] WHERE b.[BatchId] = @BatchId AND p.[Enabled] = 1 AND s.[Enabled] = 1 ) BEGIN RAISERROR('Requested execution run does not contain any enabled stages/pipelines.',16,1); RETURN 0; END;
INSERT INTO [metadata].[CurrentExecution] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName] ) SELECT @LocalExecutionId, p.[StageId], p.[PipelineId], @CallingOrchestratorName, d.[ResourceGroupName], d.[OrchestratorType], d.[OrchestratorName], p.[PipelineName] FROM [metadata].[Pipelines] p INNER JOIN [metadata].[Stages] s ON p.[StageId] = s.[StageId] INNER JOIN [metadata].[Orchestrators] d ON p.[OrchestratorId] = d.[OrchestratorId] INNER JOIN [metadata].[BatchStageLink] b ON b.[StageId] = s.[StageId] WHERE b.[BatchId] = @BatchId AND p.[Enabled] = 1 AND s.[Enabled] = 1;
SELECT @LocalExecutionId AS ExecutionId; END;
ALTER INDEX [IDX_GetPipelinesInStage] ON [metadata].[CurrentExecution] REBUILD; END;

CREATE PROCEDURE [metadata].[CreateNewExecution]
	(
	@CallingOrchestratorName NVARCHAR(200),
	@LocalExecutionId UNIQUEIDENTIFIER = NULL
	)
AS

BEGIN
	DECLARE @SQL VARCHAR(MAX) 
	DECLARE @ErrorDetail NVARCHAR(MAX)

	IF OBJECT_ID([metadata].[GetPropertyValueInternal]('ExecutionPrecursorProc')) IS NOT NULL
		BEGIN
			BEGIN TRY
				SET @SQL = [metadata].[GetPropertyValueInternal]('ExecutionPrecursorProc');
				EXEC(@SQL);
			END TRY
			BEGIN CATCH
				SELECT
					@ErrorDetail = 'Precursor procedure failed with error: ' + ERROR_MESSAGE();

				RAISERROR(@ErrorDetail,16,1);
			END CATCH
		END;
	ELSE
		BEGIN
			PRINT 'Precursor object not found in database.';
		END;
END;

BEGIN
	SET NOCOUNT ON;

	DECLARE @RestartStatus BIT
	DECLARE @BatchId UNIQUEIDENTIFIER
	DECLARE @BLocalExecutionId UNIQUEIDENTIFIER --declared here for batches

	IF @CallingOrchestratorName IS NULL
		SET @CallingOrchestratorName = 'Unknown';

	--get restart overide property	
	SELECT @RestartStatus = [metadata].[GetPropertyValueInternal]('OverideRestart')

	IF([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '0'
		BEGIN
			SET @BatchId = NULL;

			--check for running execution
			IF EXISTS
				(
				SELECT * FROM [metadata].[CurrentExecution] WHERE ISNULL([PipelineStatus],'') = 'Running'
				)
				BEGIN
					RAISERROR('There is already an execution run in progress. Stop this via the Orchestrator before restarting.',16,1);
					RETURN 0;
				END;	

			--reset and restart execution
			IF EXISTS
				(
				SELECT 1 FROM [metadata].[CurrentExecution] WHERE ISNULL([PipelineStatus],'') <> 'Success'
				) 
				AND @RestartStatus = 0
				BEGIN
					EXEC [metadata].[ResetExecution]
				END
			--capture failed execution and run new anyway
			ELSE IF EXISTS
				(
				SELECT 1 FROM [metadata].[CurrentExecution]
				)
				AND @RestartStatus = 1
				BEGIN
					EXEC [metadata].[UpdateExecutionLog]
						@PerformErrorCheck = 0; --Special case when OverideRestart = 1;

					EXEC [metadata].[CreateNewExecution]
						@CallingOrchestratorName = @CallingOrchestratorName
				END
			--no restart considerations, just create new execution
			ELSE
				BEGIN
					EXEC [metadata].[CreateNewExecution]
						@CallingOrchestratorName = @CallingOrchestratorName
				END
		END
	ELSE IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1'
		BEGIN						
			IF @BatchName IS NULL
				BEGIN
					RAISERROR('A NULL batch name cannot be passed when the UseExecutionBatches property is set to 1 (true).',16,1);
					RETURN 0;
				END;

			SELECT 
				@BatchId = [BatchId]
			FROM
				[metadata].[Batches]
			WHERE
				[BatchName] = @BatchName;

			--create local execution id now for the batch
			EXEC [metadata].[BatchWrapper]
				@BatchId = @BatchId,
				@LocalExecutionId = @BLocalExecutionId OUTPUT;

			--reset and restart execution
			IF EXISTS
				(
				SELECT 1 
				FROM 
					[metadata].[CurrentExecution]
				WHERE 
					[LocalExecutionId] = @BLocalExecutionId 
					AND ISNULL([PipelineStatus],'') <> 'Success'
				) 
				AND @RestartStatus = 0
				BEGIN
					EXEC [metadata].[ResetExecution]
						@LocalExecutionId = @BLocalExecutionId;
				END
			--capture failed execution and run new anyway
			ELSE IF EXISTS
				(
				SELECT 1 
				FROM 
					[metadata].[CurrentExecution]
				WHERE
					[LocalExecutionId] = @BLocalExecutionId 
				)
				AND @RestartStatus = 1
				BEGIN
					EXEC [metadata].[UpdateExecutionLog]
						@PerformErrorCheck = 0, --Special case when OverideRestart = 1;
						@ExecutionId = @BLocalExecutionId;

					EXEC [metadata].[CreateNewExecution]
						@CallingOrchestratorName = @CallingOrchestratorName,
						@LocalExecutionId = @BLocalExecutionId;
				END
			--no restart considerations, just create new execution
			ELSE
				BEGIN
					EXEC [metadata].[CreateNewExecution]
						@CallingOrchestratorName = @CallingOrchestratorName,
						@LocalExecutionId = @BLocalExecutionId;
				END

		END
	ELSE
		BEGIN
			--metadata integrity checks should mean this condition is never hit
			RAISERROR('Unknown batch handling configuration. Update properties with UseExecutionBatches value and try again.',16,1);
			RETURN 0;
		END
END;

CREATE PROCEDURE [metadata].[ExecutionWrapper]
	(
	@CallingOrchestratorName NVARCHAR(200) = NULL,
	@BatchName VARCHAR(255) = NULL
	)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @ToRecipients NVARCHAR(MAX) = ''
	DECLARE @CcRecipients NVARCHAR(MAX) = ''
	DECLARE @BccRecipients NVARCHAR(MAX) = ''
	DECLARE @EmailSubject NVARCHAR(500)
	DECLARE	@EmailBody NVARCHAR(MAX)
	DECLARE @EmailImportance VARCHAR(5)
	DECLARE @OutcomeBitValue INT

	--map pipeline status to alert outcome bit value
	SELECT
		@OutcomeBitValue = ao.[BitValue]
	FROM
		[metadata].[CurrentExecution] ce
		INNER JOIN [metadata].[AlertOutcomes] ao
			ON ce.[PipelineStatus] = ao.[PipelineOutcomeStatus]
	WHERE
		ce.[PipelineId] = @PipelineId;

	--get to recipients
	SELECT
		@ToRecipients += r.[EmailAddress] + ','
	FROM
		[metadata].[PipelineAlertLink] al
		INNER JOIN [metadata].[Recipients] r
			ON al.[RecipientId] = r.[RecipientId]
	WHERE
		al.[PipelineId] = @PipelineId
		AND al.[Enabled] = 1
		AND r.[Enabled] = 1
		AND UPPER(r.[MessagePreference]) = 'TO'
		AND (
			al.[OutcomesBitValue] & @OutcomeBitValue <> 0
			OR al.[OutcomesBitValue] & 1 <> 0 --all
			);

	IF (@ToRecipients <> '') SET @ToRecipients = LEFT(@ToRecipients,LEN(@ToRecipients)-1);

	--get cc recipients
	SELECT
		@CcRecipients += r.[EmailAddress] + ','
	FROM
		[metadata].[PipelineAlertLink] al
		INNER JOIN [metadata].[Recipients] r
			ON al.[RecipientId] = r.[RecipientId]
	WHERE
		al.[PipelineId] = @PipelineId
		AND al.[Enabled] = 1
		AND r.[Enabled] = 1
		AND UPPER(r.[MessagePreference]) = 'CC'
		AND (
			al.[OutcomesBitValue] & @OutcomeBitValue <> 0
			OR al.[OutcomesBitValue] & 1 <> 0 --all
			);

	IF (@CcRecipients <> '') SET @CcRecipients = LEFT(@CcRecipients,LEN(@CcRecipients)-1);

	--get bcc recipients
	SELECT
		@BccRecipients += r.[EmailAddress] + ','
	FROM
		[metadata].[PipelineAlertLink] al
		INNER JOIN [metadata].[Recipients] r
			ON al.[RecipientId] = r.[RecipientId]
	WHERE
		al.[PipelineId] = @PipelineId
		AND al.[Enabled] = 1
		AND r.[Enabled] = 1
		AND UPPER(r.[MessagePreference]) = 'BCC'
		AND (
			al.[OutcomesBitValue] & @OutcomeBitValue <> 0
			OR al.[OutcomesBitValue] & 1 <> 0 --all
			);

	IF (@BccRecipients <> '') SET @BccRecipients = LEFT(@BccRecipients,LEN(@BccRecipients)-1);

	--get email template
	SELECT
		@EmailBody = [PropertyValue]
	FROM
		[metadata].[CurrentProperties]
	WHERE
		[PropertyName] = 'EmailAlertBodyTemplate';

	--set subject, body and importance
	SELECT TOP (1)
		--subject
		@EmailSubject = 'metadata Alert: ' + [PipelineName] + ' - ' + [PipelineStatus],

		--body
		@EmailBody = REPLACE(@EmailBody,'##PipelineName###',[PipelineName]),
		@EmailBody = REPLACE(@EmailBody,'##Status###',[PipelineStatus]),
		@EmailBody = REPLACE(@EmailBody,'##ExecId###',CAST([LocalExecutionId] AS VARCHAR(36))),
		@EmailBody = REPLACE(@EmailBody,'##RunId###',CAST([PipelineRunId] AS VARCHAR(36))),
		@EmailBody = REPLACE(@EmailBody,'##StartDateTime###',CONVERT(VARCHAR(30), [StartDateTime], 120)),
		@EmailBody = CASE
						WHEN [EndDateTime] IS NULL THEN REPLACE(@EmailBody,'##EndDateTime###','N/A')
						ELSE REPLACE(@EmailBody,'##EndDateTime###',CONVERT(VARCHAR(30), [EndDateTime], 120))
					END,
		@EmailBody = CASE
						WHEN [EndDateTime] IS NULL THEN REPLACE(@EmailBody,'##Duration###','N/A')
						ELSE REPLACE(@EmailBody,'##Duration###',CAST(DATEDIFF(MINUTE, [StartDateTime], [EndDateTime]) AS VARCHAR(30)))
					END,
		@EmailBody = REPLACE(@EmailBody,'##CalledByOrc###',[CallingOrchestratorName]),
		@EmailBody = REPLACE(@EmailBody,'##ExecutedByOrcType###',[OrchestratorType]),
		@EmailBody = REPLACE(@EmailBody,'##ExecutedByOrc###',[OrchestratorName]),

		--importance
		@EmailImportance = 
			CASE [PipelineStatus] 
				WHEN 'Success' THEN 'Low'
				WHEN 'Failed' THEN 'High'
				ELSE 'Normal'
			END
	FROM
		[metadata].[CurrentExecution]
	WHERE
		[PipelineId] = @PipelineId
	ORDER BY
		[StartDateTime] DESC;

	--precaution
	IF @EmailBody IS NULL
		SET @EmailBody = 'Internal error. Failed to create profwk email alert body. Execute procedure [metadata].[GetEmailAlertParts] with pipeline Id: ' + CAST(@PipelineId AS VARCHAR(30)) + ' to debug.';

	--return email parts
	SELECT
		@ToRecipients AS emailRecipients,
		@CcRecipients AS emailCcRecipients,
		@BccRecipients AS emailBccRecipients,
		@EmailSubject AS emailSubject,
		@EmailBody AS emailBody,
		@EmailImportance AS emailImportance;
END;

BEGIN SET NOCOUNT ON;
DECLARE @FrameworkOrchestrator NVARCHAR(200)
--defensive check SELECT @FrameworkOrchestrator = UPPER([OrchestratorName]), @CallingOrchestratorName = UPPER(@CallingOrchestratorName) FROM [metadata].[Orchestrators] WHERE [IsFrameworkOrchestrator] = 1;
IF(@FrameworkOrchestrator <> @CallingOrchestratorName) BEGIN RAISERROR('Orchestrator mismatch. Calling orchestrator does not match expected IsFrameworkOrchestrator name.',16,1); RETURN 0; END
--orchestrator detials SELECT [SubscriptionId], [ResourceGroupName], [OrchestratorName], [OrchestratorType] FROM [metadata].[Orchestrators] WHERE [IsFrameworkOrchestrator] = 1; END;

BEGIN SET NOCOUNT ON;
DECLARE @Json VARCHAR(MAX) = ''
--get parameters if required for worker pipeline IF NOT EXISTS ( SELECT [ParameterId] FROM [metadata].[PipelineParameters] WHERE [PipelineId] = @PipelineId ) BEGIN SET @Json = '' --Can't return NULL. Would break ADF expression. END ELSE BEGIN SELECT @Json += CASE WHEN [ParameterValue] IS NULL THEN '' --don't add pair so ADF uses default ELSE '"' + [ParameterName] + '": "' + STRING_ESCAPE([ParameterValue],'json') + '",' END FROM [metadata].[PipelineParameters] WHERE [PipelineId] = @PipelineId;
--handle parameter(s) with a NULL values IF LEN(@Json) > 0 BEGIN --JSON snippet gets injected into Azure Function body request via Orchestrator expressions. --Comma used to support Orchestrator expression. SET @Json = ',"pipelineParameters": {' + LEFT(@Json,LEN(@Json)-1) + '}'
--update current execution log if this is a runtime request UPDATE [metadata].[CurrentExecution] SET --add extra braces to make JSON string valid in logs [PipelineParamsUsed] = '{ ' + RIGHT(@Json,LEN(@Json)-1) + ' }' WHERE [PipelineId] = @PipelineId;
--set last values values UPDATE [metadata].[PipelineParameters] SET [ParameterValueLastUsed] = [ParameterValue] WHERE [PipelineId] = @PipelineId; END; END;
--return JSON snippet SELECT @Json AS Params END;

BEGIN DECLARE @PropertyValue NVARCHAR(MAX)
SELECT @PropertyValue = ISNULL([PropertyValue],'') FROM [metadata].[CurrentProperties] WHERE [PropertyName] = @PropertyName
RETURN @PropertyValue END;

CREATE FUNCTION [metadata].[GetPropertyValueInternal]
	(
	@PropertyName VARCHAR(128)
	)
RETURNS NVARCHAR(MAX)
AS

BEGIN SET NOCOUNT ON;
--defensive check IF NOT EXISTS ( SELECT 1 FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId AND ISNULL([PipelineStatus],'') <> 'Success' ) BEGIN RAISERROR('Requested execution run does not contain any enabled stages/pipelines.',16,1); RETURN 0; END;
SELECT DISTINCT [StageId] FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId AND ISNULL([PipelineStatus],'') <> 'Success' ORDER BY [StageId] ASC END;

CREATE PROCEDURE [metadata].[GetWorkerAuthDetails]
	(
	@ExecutionId UNIQUEIDENTIFIER,
	@StageId INT,
	@PipelineId INT
	)
AS

BEGIN /* Created this proc just to reduce and refactor the number of pipeline activity calls needed due to the Microsoft enforced limit of 40 activities per pipeline. */ SET NOCOUNT ON;
DECLARE @WorkerAuthDetails TABLE ( [tenantId] UNIQUEIDENTIFIER NULL, [applicationId] NVARCHAR(MAX) NULL, [authenticationKey] NVARCHAR(MAX) NULL, [subscriptionId] UNIQUEIDENTIFIER NULL )
DECLARE @WorkerDetails TABLE ( [resourceGroupName] NVARCHAR(200) NULL, [orchestratorName] NVARCHAR(200) NULL, [orchestratorType] CHAR(3) NULL, [pipelineName] NVARCHAR(200) NULL )
--get work auth details INSERT INTO @WorkerAuthDetails ( [tenantId], [subscriptionId], [applicationId], [authenticationKey] ) EXEC [metadata].[GetWorkerAuthDetails] @ExecutionId = @ExecutionId, @StageId = @StageId, @PipelineId = @PipelineId;
--get main worker details INSERT INTO @WorkerDetails ( [pipelineName], [orchestratorName], [orchestratorType], [resourceGroupName] ) EXEC [metadata].[GetWorkerPipelineDetails] @ExecutionId = @ExecutionId, @StageId = @StageId, @PipelineId = @PipelineId;
--return all details SELECT ad.[tenantId], ad.[applicationId], ad.[authenticationKey], ad.[subscriptionId], d.[resourceGroupName], d.[orchestratorName], d.[orchestratorType], d.[pipelineName] FROM @WorkerDetails d CROSS JOIN @WorkerAuthDetails ad; END;

