	)
AS

CREATE PROCEDURE [metadata].[GetWorkerPipelineDetails]
	(
	@ExecutionId UNIQUEIDENTIFIER,
	@StageId INT,
	@PipelineId INT
	)
AS

BEGIN SET NOCOUNT ON;
IF([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '0' BEGIN --capture any pipelines that might be in an unexpected state INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], 'Unknown', [EndDateTime] FROM [metadata].[CurrentExecution] WHERE --these are predicted states [PipelineStatus] NOT IN ( 'Success', 'Failed', 'Blocked', 'Cancelled' );
--reset status ready for next attempt UPDATE [metadata].[CurrentExecution] SET [StartDateTime] = NULL, [EndDateTime] = NULL, [PipelineStatus] = NULL, [LastStatusCheckDateTime] = NULL, [PipelineRunId] = NULL, [PipelineParamsUsed] = NULL, [IsBlocked] = 0 WHERE ISNULL([PipelineStatus],'') <> 'Success' OR [IsBlocked] = 1;
--return current execution id SELECT DISTINCT [LocalExecutionId] AS ExecutionId FROM [metadata].[CurrentExecution]; END ELSE IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN --capture any pipelines that might be in an unexpected state INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], 'Unknown', [EndDateTime] FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @LocalExecutionId --these are predicted states AND [PipelineStatus] NOT IN ( 'Success', 'Failed', 'Blocked', 'Cancelled' );
--reset status ready for next attempt UPDATE [metadata].[CurrentExecution] SET [StartDateTime] = NULL, [EndDateTime] = NULL, [PipelineStatus] = NULL, [LastStatusCheckDateTime] = NULL, [PipelineRunId] = NULL, [PipelineParamsUsed] = NULL, [IsBlocked] = 0 WHERE [LocalExecutionId] = @LocalExecutionId AND ISNULL([PipelineStatus],'') <> 'Success' OR [IsBlocked] = 1;
UPDATE [metadata].[BatchExecution] SET [EndDateTime] = NULL, [BatchStatus] = 'Running' WHERE [ExecutionId] = @LocalExecutionId;
SELECT @LocalExecutionId AS ExecutionId END; END;

CREATE PROCEDURE [metadata].[SetExecutionBlockDependants]
	(
	@ExecutionId UNIQUEIDENTIFIER = NULL,
	@PipelineId INT
	)
AS

BEGIN SET NOCOUNT ON;
--mark specific failure pipeline UPDATE [metadata].[CurrentExecution] SET [PipelineStatus] = @CallingActivity + 'Error' WHERE [LocalExecutionId] = @ExecutionId AND [StageId] = @StageId AND [PipelineId] = @PipelineId
--persist failed pipeline records to long term log INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId AND [PipelineStatus] = @CallingActivity + 'Error' AND [StageId] = @StageId AND [PipelineId] = @PipelineId
--decide how to proceed with error/failure depending on framework property configuration IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'None' BEGIN --do nothing allow processing to carry on regardless RETURN 0; END;
ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'Simple' BEGIN --flag all downstream stages as blocked UPDATE [metadata].[CurrentExecution] SET [PipelineStatus] = 'Blocked', [IsBlocked] = 1 WHERE [LocalExecutionId] = @ExecutionId AND [StageId] > @StageId;
--update batch if applicable IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN UPDATE [metadata].[BatchExecution] SET [BatchStatus] = 'Stopping' --special case when its an activity failure to call stop ready for restart WHERE [ExecutionId] = @ExecutionId AND [BatchStatus] = 'Running'; END; END;
ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'DependencyChain' BEGIN EXEC [metadata].[SetExecutionBlockDependants] @ExecutionId = @ExecutionId, @PipelineId = @PipelineId END; ELSE BEGIN RAISERROR('Unknown failure handling state.',16,1); RETURN 0; END; END;

CREATE PROCEDURE [metadata].[SetLogActivityFailed]
	(
	@ExecutionId UNIQUEIDENTIFIER,
	@StageId INT,
	@PipelineId INT,
	@CallingActivity VARCHAR(255)
	)
AS

BEGIN SET NOCOUNT ON;
DECLARE @ErrorDetail VARCHAR(500);
--mark specific failure pipeline UPDATE [metadata].[CurrentExecution] SET [PipelineStatus] = 'Cancelled' WHERE [LocalExecutionId] = @ExecutionId AND [StageId] = @StageId AND [PipelineId] = @PipelineId
--no need to block and log if done during a clean up cycle IF @CleanUpRun = 1 RETURN 0;
--persist cancelled pipeline records to long term log INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId AND [PipelineStatus] = 'Cancelled' AND [StageId] = @StageId AND [PipelineId] = @PipelineId;
--block down stream stages? IF ([metadata].[GetPropertyValueInternal]('CancelledWorkerResultBlocks')) = 1 BEGIN --decide how to proceed with error/failure depending on framework property configuration IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'None' BEGIN --do nothing allow processing to carry on regardless RETURN 0; END;
ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'Simple' BEGIN --flag all downstream stages as blocked UPDATE [metadata].[CurrentExecution] SET [PipelineStatus] = 'Blocked', [IsBlocked] = 1 WHERE [LocalExecutionId] = @ExecutionId AND [StageId] > @StageId
--update batch if applicable IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN UPDATE [metadata].[BatchExecution] SET [BatchStatus] = 'Stopping' WHERE [ExecutionId] = @ExecutionId AND [BatchStatus] = 'Running'; END;
SET @ErrorDetail = 'Pipeline execution has a cancelled status. Blocking downstream stages as a precaution.'
RAISERROR(@ErrorDetail,16,1); RETURN 0; END; ELSE IF ([metadata].[GetPropertyValueInternal]('FailureHandling')) = 'DependencyChain' BEGIN EXEC [metadata].[SetExecutionBlockDependants] @ExecutionId = @ExecutionId, @PipelineId = @PipelineId END; ELSE BEGIN RAISERROR('Cancelled execution failure handling state.',16,1); RETURN 0; END; END; END;

