	@StageId INT,
	@PipelineId INT,
	@CleanUpRun BIT = 0
	)
AS

CREATE PROCEDURE [metadata].[SetLogPipelineValidating]
	(
	@ExecutionId UNIQUEIDENTIFIER,
	@StageId INT,
	@PipelineId INT
	)
AS

BEGIN SET NOCOUNT ON;
DECLARE @AllCount INT DECLARE @SuccessCount INT
IF([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '0' BEGIN IF @PerformErrorCheck = 1 BEGIN --Check current execution SELECT @AllCount = COUNT(0) FROM [metadata].[CurrentExecution] SELECT @SuccessCount = COUNT(0) FROM [metadata].[CurrentExecution] WHERE [PipelineStatus] = 'Success'
IF @AllCount <> @SuccessCount BEGIN RAISERROR('Framework execution complete but not all Worker pipelines succeeded. See the [metadata].[CurrentExecution] table for details',16,1); RETURN 0; END; END;
--Do this if no error raised and when called by the execution wrapper (OverideRestart = 1). INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] FROM [metadata].[CurrentExecution];
TRUNCATE TABLE [metadata].[CurrentExecution]; END ELSE IF ([metadata].[GetPropertyValueInternal]('UseExecutionBatches')) = '1' BEGIN IF @PerformErrorCheck = 1 BEGIN --Check current execution SELECT @AllCount = COUNT(0) FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId;
SELECT @SuccessCount = COUNT(0) FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId AND [PipelineStatus] = 'Success';
IF @AllCount <> @SuccessCount BEGIN UPDATE [metadata].[BatchExecution] SET [BatchStatus] = 'Stopped', [EndDateTime] = GETUTCDATE() WHERE [ExecutionId] = @ExecutionId;
RAISERROR('Framework execution complete for batch but not all Worker pipelines succeeded. See the [metadata].[CurrentExecution] table for details',16,1); RETURN 0; END; ELSE BEGIN UPDATE [metadata].[BatchExecution] SET [BatchStatus] = 'Success', [EndDateTime] = GETUTCDATE() WHERE [ExecutionId] = @ExecutionId; END; END; --end check
--Do this if no error raised and when called by the execution wrapper (OverideRestart = 1). INSERT INTO [metadata].[ExecutionLog] ( [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] ) SELECT [LocalExecutionId], [StageId], [PipelineId], [CallingOrchestratorName], [ResourceGroupName], [OrchestratorType], [OrchestratorName], [PipelineName], [StartDateTime], [PipelineStatus], [EndDateTime], [PipelineRunId], [PipelineParamsUsed] FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId;
DELETE FROM [metadata].[CurrentExecution] WHERE [LocalExecutionId] = @ExecutionId; END; END;

CREATE PROCEDURE [metadata].[UpdateExecutionLog]
	(
	@PerformErrorCheck BIT = 1,
	@ExecutionId UNIQUEIDENTIFIER = NULL
	)
AS

BEGIN
	SET NOCOUNT ON;

	DECLARE @PipelineId INT;
	DECLARE @DependantPipelineId INT;

	--get pipeline ids
	SELECT
		@PipelineId = [PipelineId]
	FROM
		[metadata].[Pipelines]
	WHERE
		[PipelineName] = @PipelineName;

	SELECT
		@DependantPipelineId = [PipelineId]
	FROM
		[metadata].[Pipelines]
	WHERE
		[PipelineName] = @DependantPipelineName;

	--defensive checks
	IF @PipelineId IS NULL
	BEGIN
		RAISERROR('Pipeline not found in pipelines table.', 16,1);
		RETURN 0;
	END;

	IF @DependantPipelineId IS NULL
	BEGIN
		RAISERROR('Dependant pipeline not found in pipelines table.', 16,1);
		RETURN 0;
	END;

	IF @PipelineId = @DependantPipelineId
	BEGIN
		RAISERROR('Pipeline cannot be dependant on itself.', 16,1);
		RETURN 0;
	END;

	IF EXISTS
		(
		SELECT 
			*
		FROM 
			[metadata].[Pipelines] pp
			INNER JOIN [metadata].[Pipelines] dp
				ON dp.[PipelineId] = @DependantPipelineId
		WHERE
			pp.[PipelineId] = @PipelineId
			AND pp.[StageId] = dp.[StageId]
		)
		BEGIN
			RAISERROR('Pipeline and dependent pipeline cannot be in the same execution stage.', 16,1);
			RETURN 0;
		END;

	--final soft check and insert
	IF EXISTS
		(
		SELECT 
			* 
		FROM 
			[metadata].[PipelineDependencies]
		WHERE
			[PipelineId] = @PipelineId
			AND [DependantPipelineId] = @DependantPipelineId
		)
		BEGIN
			PRINT 'Dependency already exists. Nothing added.'
			RETURN 0;
		END
	ELSE
		BEGIN
			INSERT INTO [metadata].[PipelineDependencies]
				(
				[PipelineId],
				[DependantPipelineId]
				)
			VALUES
				(
				@PipelineId,
				@DependantPipelineId
				)
		END;
END;

BEGIN SET NOCOUNT ON;
DECLARE @OrchestratorId INT DECLARE @StageId INT DECLARE @StageName VARCHAR(255) = 'PoShAdded'
--get/set orchestrator IF EXISTS ( SELECT * FROM [metadata].[Orchestrators] WHERE [OrchestratorName] = @OrchestratorName AND [ResourceGroupName] = @ResourceGroup AND [OrchestratorType] = @OrchestratorType ) BEGIN SELECT @OrchestratorId = [OrchestratorId] FROM [metadata].[Orchestrators] WHERE [OrchestratorName] = @OrchestratorName AND [ResourceGroupName] = @ResourceGroup AND [OrchestratorType] = @OrchestratorType; END ELSE BEGIN INSERT INTO [metadata].[Orchestrators] ( [OrchestratorName], [OrchestratorType], [ResourceGroupName], [Description], [SubscriptionId] ) VALUES ( @OrchestratorName, @OrchestratorType, @ResourceGroup, 'Added via PowerShell.', '12345678-1234-1234-1234-012345678910' )
SELECT @OrchestratorId = SCOPE_IDENTITY(); END
--get/set stage IF EXISTS ( SELECT * FROM [metadata].[Stages] WHERE [StageName] = @StageName ) BEGIN SELECT @StageId = [StageId] FROM [metadata].[Stages] WHERE [StageName] = @StageName; END; ELSE BEGIN INSERT INTO [metadata].[Stages] ( [StageName], [StageDescription], [Enabled] ) VALUES ( @StageName, 'Added via PowerShell.', 1 );
SELECT @StageId = SCOPE_IDENTITY(); END;
--upsert pipeline ;WITH sourceData AS ( SELECT @OrchestratorId AS OrchestratorId, @PipelineName AS PipelineName, @StageId AS StageId, NULL AS LogicalPredecessorId, 1 AS [Enabled] ) MERGE INTO [metadata].[Pipelines] AS tgt USING sourceData AS src ON tgt.[OrchestratorId] = src.[OrchestratorId] AND tgt.[PipelineName] = src.[PipelineName] WHEN MATCHED THEN UPDATE SET tgt.[StageId] = src.[StageId], tgt.[LogicalPredecessorId] = src.[LogicalPredecessorId], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [OrchestratorId], [StageId], [PipelineName], [LogicalPredecessorId], [Enabled] ) VALUES ( src.[OrchestratorId], src.[StageId], src.[PipelineName], src.[LogicalPredecessorId], src.[Enabled] ); END;

CREATE PROCEDURE [metadataHelpers].[AddPipelineViaPowerShell]
	(
	@ResourceGroup NVARCHAR(200),
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3) = 'ADF',
	@PipelineName NVARCHAR(200)
	)
AS

BEGIN
SET NOCOUNT ON;
--defensive check IF EXISTS ( SELECT * FROM [metadata].[Properties] WHERE [PropertyName] = @PropertyName AND [ValidTo] IS NOT NULL ) AND NOT EXISTS ( SELECT * FROM [metadata].[Properties] WHERE [PropertyName] = @PropertyName AND [ValidTo] IS NULL ) BEGIN WITH lastValue AS ( SELECT [PropertyId], ROW_NUMBER() OVER (PARTITION BY [PropertyName] ORDER BY [ValidTo] ASC) AS Rn FROM [metadata].[Properties] WHERE [PropertyName] = @PropertyName ) --reset property if valid to date has been incorrectly set UPDATE prop SET [ValidTo] = NULL FROM [metadata].[Properties] prop INNER JOIN lastValue ON prop.[PropertyId] = lastValue.[PropertyId] WHERE lastValue.[Rn] = 1 END
--upsert property ;WITH sourceTable AS ( SELECT @PropertyName AS PropertyName, @PropertyValue AS PropertyValue, @Description AS [Description], GETUTCDATE() AS StartEndDate ) --insert new version of existing property from MERGE OUTPUT INSERT INTO [metadata].[Properties] ( [PropertyName], [PropertyValue], [Description], [ValidFrom] ) SELECT [PropertyName], [PropertyValue], [Description], GETUTCDATE() FROM ( MERGE INTO [metadata].[Properties] targetTable USING sourceTable ON sourceTable.[PropertyName] = targetTable.[PropertyName] --set valid to date on existing property WHEN MATCHED AND [ValidTo] IS NULL THEN UPDATE SET targetTable.[ValidTo] = sourceTable.[StartEndDate] --add new property WHEN NOT MATCHED BY TARGET THEN INSERT ( [PropertyName], [PropertyValue], [Description], [ValidFrom] ) VALUES ( sourceTable.[PropertyName], sourceTable.[PropertyValue], sourceTable.[Description], sourceTable.[StartEndDate] ) --for new entry of existing record OUTPUT $action AS [Action], sourceTable.* ) AS MergeOutput WHERE MergeOutput.[Action] = 'UPDATE'; END;

CREATE PROCEDURE [metadataHelpers].[AddProperty]
	(
	@PropertyName VARCHAR(128),
	@PropertyValue NVARCHAR(MAX),
	@Description NVARCHAR(MAX) = NULL
	)
AS

BEGIN SET NOCOUNT ON;
DECLARE @ActualBitValue INT DECLARE @SQL NVARCHAR(MAX) = '' DECLARE @BitValue TABLE ([TotalBitValue] INT NOT NULL);
--get alert status bit value SET @AlertForStatus = LTRIM(RTRIM(@AlertForStatus)) SET @AlertForStatus = REPLACE(@AlertForStatus,' ','') SET @AlertForStatus = '''' + REPLACE(@AlertForStatus,',',''',''') + ''''
SET @SQL = ' SELECT SUM([BitValue]) AS ''TotalBitValue'' FROM [metadata].[AlertOutcomes] WHERE [PipelineOutcomeStatus] IN (' + @AlertForStatus + ') '
INSERT INTO @BitValue ([TotalBitValue]) EXECUTE(@SQL) SELECT @ActualBitValue = [TotalBitValue] FROM @BitValue
--set link table IF @PipelineName IS NOT NULL BEGIN --add alert for specific pipeline if doesn't exist INSERT INTO [metadata].[PipelineAlertLink] ( [PipelineId], [RecipientId], [OutcomesBitValue] ) SELECT p.[PipelineId], r.[RecipientId], @ActualBitValue FROM [metadata].[Pipelines] p INNER JOIN [metadata].[Recipients] r ON r.[Name] = @RecipientName LEFT OUTER JOIN [metadata].[PipelineAlertLink] al ON p.[PipelineId] = al.[PipelineId] AND r.[RecipientId] = al.[RecipientId] WHERE p.[PipelineName] = @PipelineName AND al.[PipelineId] IS NULL AND al.[RecipientId] IS NULL; END ELSE IF @PipelineName IS NULL BEGIN --remove and re-add alerts for all pipelines DELETE al FROM [metadata].[PipelineAlertLink] al INNER JOIN [metadata].[Recipients] r ON al.[RecipientId] = r.[RecipientId] WHERE r.[Name] = @RecipientName;
INSERT INTO [metadata].[PipelineAlertLink] ( [PipelineId], [RecipientId], [OutcomesBitValue] ) SELECT p.[PipelineId], r.[RecipientId], @ActualBitValue FROM [metadata].[Recipients] r CROSS JOIN [metadata].[Pipelines] p WHERE r.[Name] = @RecipientName; END; END

CREATE PROCEDURE [metadataHelpers].[AddRecipientPipelineAlerts]
	(
	@RecipientName VARCHAR(255),
	@PipelineName NVARCHAR(200) = NULL,
	@AlertForStatus NVARCHAR(500) = 'All'
	)
AS

CREATE PROCEDURE [metadataHelpers].[AddServicePrincipal]
	(
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3),
	@PrincipalId NVARCHAR(256),
	@PrincipalSecret NVARCHAR(MAX),
	@SpecificPipelineName NVARCHAR(200) = NULL,
	@PrincipalName NVARCHAR(256) = NULL
	)
AS

CREATE PROCEDURE [metadataHelpers].[AddServicePrincipalUrls]
	(
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3),
	@PrincipalIdUrl NVARCHAR(MAX),
	@PrincipalSecretUrl NVARCHAR(MAX),
	@SpecificPipelineName NVARCHAR(200) = NULL,
	@PrincipalName NVARCHAR(256) = NULL
	)
AS

BEGIN

	IF ([metadata].[GetPropertyValueInternal]('SPNHandlingMethod')) = 'StoreInDatabase'
		BEGIN
			EXEC [metadata].[AddServicePrincipal]
				@OrchestratorName = @OrchestratorName,
				@OrchestratorType = @OrchestratorType,
				@PrincipalId = @PrincipalIdValue,
				@PrincipalSecret = @PrincipalSecretValue,
				@PrincipalName = @PrincipalName,
				@SpecificPipelineName = @SpecificPipelineName			
		END
	ELSE IF ([metadata].[GetPropertyValueInternal]('SPNHandlingMethod')) = 'StoreInKeyVault'
		BEGIN
			EXEC [metadata].[AddServicePrincipalUrls]
				@OrchestratorName = @OrchestratorName,
				@OrchestratorType = @OrchestratorType,
				@PrincipalIdUrl = @PrincipalIdValue,
				@PrincipalSecretUrl = @PrincipalSecretValue,
				@PrincipalName = @PrincipalName,
				@SpecificPipelineName = @SpecificPipelineName		
		END
	ELSE
		BEGIN
			RAISERROR('Unknown SPN insert method.',16,1);
			RETURN 0;
		END
END;

CREATE PROCEDURE [metadataHelpers].[AddServicePrincipalWrapper]
	(
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3),
	@PrincipalIdValue NVARCHAR(MAX),
	@PrincipalSecretValue NVARCHAR(MAX),
	@SpecificPipelineName NVARCHAR(200) = NULL,
	@PrincipalName NVARCHAR(256) = NULL
	)
AS

BEGIN
	/*
	DELETE ORDER IMPORTANT FOR REFERENTIAL INTEGRITY
	*/

	--BatchExecution
	IF OBJECT_ID(N'[metadata].[BatchExecution]') IS NOT NULL
		BEGIN
			TRUNCATE TABLE [metadata].[BatchExecution];
		END;

	--CurrentExecution
	IF OBJECT_ID(N'[metadata].[CurrentExecution]') IS NOT NULL
		BEGIN
			TRUNCATE TABLE [metadata].[CurrentExecution];
		END;

	--ExecutionLog
	IF OBJECT_ID(N'[metadata].[ExecutionLog]') IS NOT NULL
		BEGIN
			TRUNCATE TABLE [metadata].[ExecutionLog];
		END

	--ErrorLog
	IF OBJECT_ID(N'[metadata].[ExecutionLog]') IS NOT NULL
		BEGIN
			TRUNCATE TABLE [metadata].[ErrorLog];
		END

	--BatchStageLink
	IF OBJECT_ID(N'[metadata].[BatchStageLink]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[BatchStageLink];
		END;

	--Batches
	IF OBJECT_ID(N'[metadata].[Batches]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Batches];
		END;

	--PipelineDependencies
	IF OBJECT_ID(N'[metadata].[PipelineDependencies]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[PipelineDependencies];
			DBCC CHECKIDENT ('[metadata].[PipelineDependencies]', RESEED, 0);
		END;

	--PipelineAlertLink
	IF OBJECT_ID(N'[metadata].[PipelineAlertLink]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[PipelineAlertLink];
			DBCC CHECKIDENT ('[metadata].[PipelineAlertLink]', RESEED, 0);
		END;

	--Recipients
	IF OBJECT_ID(N'[metadata].[Recipients]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Recipients];
			DBCC CHECKIDENT ('[metadata].[Recipients]', RESEED, 0);
		END;

	--AlertOutcomes
	IF OBJECT_ID(N'[metadata].[AlertOutcomes]') IS NOT NULL
		BEGIN
			TRUNCATE TABLE [metadata].[AlertOutcomes];
		END;

	--PipelineAuthLink
	IF OBJECT_ID(N'[metadata].[PipelineAuthLink]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[PipelineAuthLink];
			DBCC CHECKIDENT ('[metadata].[PipelineAuthLink]', RESEED, 0);
		END;

	--ServicePrincipals
	IF OBJECT_ID(N'[dbo].[ServicePrincipals]') IS NOT NULL 
		BEGIN
			DELETE FROM [dbo].[ServicePrincipals];
			DBCC CHECKIDENT ('[dbo].[ServicePrincipals]', RESEED, 0);
		END;

	--Properties
	IF OBJECT_ID(N'[metadata].[Properties]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Properties];
			DBCC CHECKIDENT ('[metadata].[Properties]', RESEED, 0);
		END;

	--PipelineParameters
	IF OBJECT_ID(N'[metadata].[PipelineParameters]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[PipelineParameters];
			DBCC CHECKIDENT ('[metadata].[PipelineParameters]', RESEED, 0);
		END;

	--Pipelines
	IF OBJECT_ID(N'[metadata].[Pipelines]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Pipelines];
			DBCC CHECKIDENT ('[metadata].[Pipelines]', RESEED, 0);
		END;

	--Orchestrators
	IF OBJECT_ID(N'[metadata].[Orchestrators]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Orchestrators];
			DBCC CHECKIDENT ('[metadata].[Orchestrators]', RESEED, 0);
		END;

	--Stages
	IF OBJECT_ID(N'[metadata].[Stages]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Stages];
			DBCC CHECKIDENT ('[metadata].[Stages]', RESEED, 0);
		END;

	--Subscriptions
	IF OBJECT_ID(N'[metadata].[Subscriptions]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Subscriptions];
		END;

	--Tenants
	IF OBJECT_ID(N'[metadata].[Tenants]') IS NOT NULL
		BEGIN
			DELETE FROM [metadata].[Tenants];
		END;
END;

BEGIN SET NOCOUNT ON;
--defensive check IF NOT EXISTS ( SELECT [RecipientId] FROM [metadata].[Recipients] WHERE [EmailAddress] = @EmailAddress ) BEGIN RAISERROR('Recipient email address does not exists in [metadata].[Recipients] table.',16,1); RETURN 0; END;
--update/delete IF @SoftDeleteOnly = 1 BEGIN --disable links UPDATE al SET al.[Enabled] = 0 FROM [metadata].[PipelineAlertLink] al INNER JOIN [metadata].[Recipients] r ON al.[RecipientId] = r.[RecipientId] WHERE r.[EmailAddress] = @EmailAddress;
--disable recipient(s) UPDATE [metadata].[Recipients] SET [Enabled] = 0 WHERE [EmailAddress] = @EmailAddress;
END ELSE BEGIN --delete links DELETE al FROM [metadata].[PipelineAlertLink] al INNER JOIN [metadata].[Recipients] r ON al.[RecipientId] = r.[RecipientId] WHERE r.[EmailAddress] = @EmailAddress;
--delete recipient(s) DELETE FROM [metadata].[Recipients] WHERE [EmailAddress] = @EmailAddress; END; END;

BEGIN SET NOCOUNT ON;
DECLARE @ErrorDetails NVARCHAR(500) = '' DECLARE @CredentialId INT
--resolve principal Id or Url to credential Id IF ([metadata].[GetPropertyValueInternal]('SPNHandlingMethod')) = 'StoreInDatabase' BEGIN --defensive checks BEGIN TRY DECLARE @LocalPrincipalId UNIQUEIDENTIFIER
SELECT --assigned to variable just to supress output of SELECT @LocalPrincipalId = CAST(@PrincipalIdValue AS UNIQUEIDENTIFIER) END TRY BEGIN CATCH SET @ErrorDetails = 'Invalid @PrincipalId provided. The format must be a UNIQUEIDENTIFIER.' RAISERROR(@ErrorDetails, 16, 1); RETURN 0; END CATCH
--get cred id using principal id SELECT @CredentialId = [CredentialId] FROM [dbo].[ServicePrincipals] WHERE [PrincipalId] = @PrincipalIdValue END ELSE IF ([metadata].[GetPropertyValueInternal]('SPNHandlingMethod')) = 'StoreInKeyVault' BEGIN --get cred id using principal id url SELECT @CredentialId = [CredentialId] FROM [dbo].[ServicePrincipals] WHERE [PrincipalIdUrl] = @PrincipalIdValue END; ELSE BEGIN RAISERROR('Unknown SPN deletion method.',16,1); RETURN 0; END;
--secondary defensive checks IF NOT EXISTS ( SELECT [OrchestratorName] FROM [metadata].[Orchestrators] WHERE [OrchestratorName] = @OrchestratorName AND [OrchestratorType] = @OrchestratorType ) BEGIN SET @ErrorDetails = 'Invalid Orchestrator name. Please ensure the Orchestrator metadata exists.' RAISERROR(@ErrorDetails, 16, 1); RETURN 0; END
IF @CredentialId IS NULL BEGIN SET @ErrorDetails = 'Invalid Service Principal Id Value provided. Please ensure the Service Principal exists.' RAISERROR(@ErrorDetails, 16, 1); RETURN 0; END
--delete SPN for specific pipeline IF @SpecificPipelineName IS NOT NULL BEGIN IF NOT EXISTS ( SELECT [PipelineName] FROM [metadata].[Pipelines] WHERE [PipelineName] = @SpecificPipelineName ) BEGIN SET @ErrorDetails = 'Invalid Pipeline name. Please ensure the Pipeline metadata exists.' RAISERROR(@ErrorDetails, 16, 1); RETURN 0; END
--delete links DELETE L FROM [metadata].[PipelineAuthLink] L INNER JOIN [metadata].[Pipelines] P ON L.[PipelineId] = P.[PipelineId] INNER JOIN [metadata].[Orchestrators] D ON P.[OrchestratorId] = D.[OrchestratorId] AND L.[OrchestratorId] = D.[OrchestratorId] INNER JOIN [dbo].[ServicePrincipals] S ON L.[CredentialId] = S.[CredentialId] WHERE P.[PipelineName] = @SpecificPipelineName AND D.[OrchestratorName] = @OrchestratorName AND D.[OrchestratorType] = @OrchestratorType AND S.[CredentialId] = @CredentialId; END ELSE BEGIN --delete links DELETE L FROM [metadata].[PipelineAuthLink] L INNER JOIN [metadata].[Orchestrators] D ON L.[OrchestratorId] = D.[OrchestratorId] INNER JOIN [dbo].[ServicePrincipals] S ON L.[CredentialId] = S.[CredentialId] WHERE D.[OrchestratorName] = @OrchestratorName AND D.[OrchestratorType] = @OrchestratorType AND S.[CredentialId] = @CredentialId; END
--finall, delete principal only if not still used by other pipelines DELETE SP FROM [dbo].[ServicePrincipals] SP LEFT OUTER JOIN [metadata].[PipelineAuthLink] AL ON SP.[CredentialId] = AL.[CredentialId] WHERE SP.[CredentialId] = @CredentialId AND AL.[CredentialId] IS NULL; END;

CREATE PROCEDURE [metadataHelpers].[DeleteServicePrincipal]
	(
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3),
	@PrincipalIdValue NVARCHAR(256),
	@SpecificPipelineName NVARCHAR(200) = NULL
	)
AS

BEGIN
--Get last execution ID IF @LocalExecutionId IS NULL BEGIN WITH maxLog AS ( SELECT MAX([LogId]) AS MaxLogId FROM [metadata].[ExecutionLog] ) SELECT @LocalExecutionId = el1.[LocalExecutionId] FROM [metadata].[ExecutionLog] el1 INNER JOIN maxLog ON maxLog.[MaxLogId] = el1.[LogId]; END;
--Execution Summary SELECT CAST(el2.[StageId] AS VARCHAR(5)) + ' - ' + stgs.[StageName] AS Stage, COUNT(0) AS RecordCount, DATEDIFF(MINUTE, MIN(el2.[StartDateTime]), MAX(el2.[EndDateTime])) DurationMinutes FROM [metadata].[ExecutionLog] el2 INNER JOIN [metadata].[Stages] stgs ON el2.[StageId] = stgs.[StageId] WHERE el2.[LocalExecutionId] = @LocalExecutionId GROUP BY CAST(el2.[StageId] AS VARCHAR(5)) + ' - ' + stgs.[StageName] ORDER BY CAST(el2.[StageId] AS VARCHAR(5)) + ' - ' + stgs.[StageName];
--Full execution details SELECT el3.[LogId], el3.[LocalExecutionId], el3.[OrchestratorType], el3.[OrchestratorName], el3.[StageId], stgs.[StageName], el3.[PipelineId], el3.[PipelineName], el3.[StartDateTime], el3.[EndDateTime], ISNULL(DATEDIFF(MINUTE, el3.[StartDateTime], el3.[EndDateTime]),0) AS DurationMinutes, el3.[PipelineStatus], el3.[PipelineRunId], el3.[PipelineParamsUsed], errLog.[ActivityRunId], errLog.[ActivityName], errLog.[ActivityType], errLog.[ErrorCode], errLog.[ErrorType], errLog.[ErrorMessage] FROM [metadata].[ExecutionLog] el3 LEFT OUTER JOIN [metadata].[ErrorLog] errLog ON el3.[LocalExecutionId] = errLog.[LocalExecutionId] AND el3.[PipelineRunId] = errLog.[PipelineRunId] INNER JOIN [metadata].[Stages] stgs ON el3.[StageId] = stgs.[StageId] WHERE el3.[LocalExecutionId] = @LocalExecutionId ORDER BY el3.[PipelineId], el3.[StartDateTime]; END;

CREATE PROCEDURE [metadataHelpers].[GetServicePrincipal]
	(
	@OrchestratorName NVARCHAR(200),
	@OrchestratorType CHAR(3),
	@PipelineName NVARCHAR(200) = NULL
	)
AS

BEGIN
	TRUNCATE TABLE [metadata].[AlertOutcomes];

	INSERT INTO [metadata].[AlertOutcomes]
		(
		[PipelineOutcomeStatus]
		)
	VALUES 
		('All'),
		('Success'),
		('Failed'),
		('Unknown'),
		('Cancelled');
END;

BEGIN DECLARE @Batches TABLE ( [BatchName] [VARCHAR](225) NOT NULL, [BatchDescription] [VARCHAR](4000) NULL, [Enabled] [BIT] NOT NULL )
INSERT @Batches ( [BatchName], [BatchDescription], [Enabled] ) VALUES ('Daily', N'Daily Worker Pipelines.', 1), ('Hourly', N'Hourly Worker Pipelines.', 1);
MERGE INTO [metadata].[Batches] AS tgt USING @Batches AS src ON tgt.[BatchName] = src.[BatchName] WHEN MATCHED THEN UPDATE SET tgt.[BatchDescription] = src.[BatchDescription], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [BatchName], [BatchDescription], [Enabled] ) VALUES ( src.[BatchName], src.[BatchDescription], src.[Enabled] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN DECLARE @Orchestrators TABLE ( [OrchestratorName] NVARCHAR(200) NOT NULL, [OrchestratorType] CHAR(3) NOT NULL, [IsFrameworkOrchestrator] BIT NOT NULL, [ResourceGroupName] NVARCHAR(200) NOT NULL, [SubscriptionId] UNIQUEIDENTIFIER NOT NULL, [Description] NVARCHAR(MAX) NULL )
INSERT INTO @Orchestrators ( [OrchestratorName], [OrchestratorType], [IsFrameworkOrchestrator], [Description], [ResourceGroupName], [SubscriptionId] ) VALUES ('FrameworkFactory','ADF',1,'Example Data Factory used for development.','ADF.metadata','12345678-1234-1234-1234-012345678910'), ('FrameworkFactoryDev','ADF',0,'Example Data Factory used for development deployments.','ADF.metadata','12345678-1234-1234-1234-012345678910'), ('FrameworkFactoryTest','ADF',0,'Example Data Factory used for testing.','ADF.metadata','12345678-1234-1234-1234-012345678910'), ('WorkersFactory','ADF',0,'Example Data Factory used to house worker pipelines.','ADF.metadata','12345678-1234-1234-1234-012345678910'), ('metadataforsynapse','SYN',0,'Example Synapse instance used to house all pipelines.','ADF.metadata','12345678-1234-1234-1234-012345678910');
MERGE INTO [metadata].[Orchestrators] AS tgt USING @Orchestrators AS src ON tgt.[OrchestratorName] = src.[OrchestratorName] AND tgt.[OrchestratorType] = src.[OrchestratorType] WHEN MATCHED THEN UPDATE SET tgt.[IsFrameworkOrchestrator] = src.[IsFrameworkOrchestrator], tgt.[Description] = src.[Description], tgt.[ResourceGroupName] = src.[ResourceGroupName], tgt.[SubscriptionId] = src.[SubscriptionId] WHEN NOT MATCHED BY TARGET THEN INSERT ( [OrchestratorName], [OrchestratorType], [IsFrameworkOrchestrator], [Description], [ResourceGroupName], [SubscriptionId] ) VALUES ( src.[OrchestratorName], src.[OrchestratorType], src.[IsFrameworkOrchestrator], src.[Description], src.[ResourceGroupName], src.[SubscriptionId] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN
	EXEC [metadataHelpers].[AddPipelineDependant]
		@PipelineName = 'Intentional Error',
		@DependantPipelineName = 'Wait 5';

	EXEC [metadataHelpers].[AddPipelineDependant]
		@PipelineName = 'Intentional Error',
		@DependantPipelineName = 'Wait 6';

	EXEC [metadataHelpers].[AddPipelineDependant]
		@PipelineName = 'Wait 6',
		@DependantPipelineName = 'Wait 9';

	EXEC [metadataHelpers].[AddPipelineDependant]
		@PipelineName = 'Wait 9',
		@DependantPipelineName = 'Wait 10';
END;

BEGIN DECLARE @PipelineParameters TABLE ( [PipelineId] [INT] NOT NULL, [ParameterName] [VARCHAR](128) NOT NULL, [ParameterValue] [NVARCHAR](MAX) NULL )
INSERT @PipelineParameters ( [PipelineId], [ParameterName], [ParameterValue] ) VALUES (1, 'WaitTime', '3'), (2, 'WaitTime', '6'), (6, 'WaitTime', '9'), (4, 'WaitTime', '5'), (5, 'WaitTime', '2'), (3, 'RaiseErrors', 'false'), (3, 'WaitTime', '10'), (7, 'WaitTime', '3'), (8, 'WaitTime', '5'), (9, 'WaitTime', '7'), (11, 'WaitTime', '10');
MERGE INTO [metadata].[PipelineParameters] AS tgt USING @PipelineParameters AS src ON tgt.[PipelineId] = src.[PipelineId] AND tgt.[ParameterName] = src.[ParameterName] WHEN MATCHED THEN UPDATE SET tgt.[ParameterValue] = src.[ParameterValue] WHEN NOT MATCHED BY TARGET THEN INSERT ( [PipelineId], [ParameterName], [ParameterValue] ) VALUES ( src.[PipelineId], src.[ParameterName], src.[ParameterValue] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN DECLARE @Pipelines TABLE ( [OrchestratorId] [INT] NOT NULL, [StageId] [INT] NOT NULL, [PipelineName] [NVARCHAR](200) NOT NULL, [LogicalPredecessorId] [INT] NULL, [Enabled] [BIT] NOT NULL )
INSERT @Pipelines ( [OrchestratorId], [StageId], [PipelineName], [LogicalPredecessorId], [Enabled] ) VALUES (1,1 ,'Wait 1' ,NULL ,1), (1,1 ,'Wait 2' ,NULL ,1), (1,1 ,'Intentional Error' ,NULL ,1), (1,1 ,'Wait 3' ,NULL ,1), (1,2 ,'Wait 4' ,NULL ,1), (1,2 ,'Wait 5' ,1 ,1), (1,2 ,'Wait 6' ,1 ,1), (1,2 ,'Wait 7' ,NULL ,1), (1,3 ,'Wait 8' ,1 ,1), (1,3 ,'Wait 9' ,6 ,1), (1,4 ,'Wait 10' ,9 ,1), --speed (1,5 ,'Wait 1' ,NULL ,0), (1,5 ,'Wait 2' ,NULL ,0), (1,5 ,'Wait 3' ,NULL ,0), (1,5 ,'Wait 4' ,NULL ,0), --synapse (5,1 ,'Wait 1' ,NULL ,1), (5,1 ,'Wait 2' ,NULL ,1), (5,1 ,'Wait 3' ,NULL ,1), (5,1 ,'Wait 4' ,NULL ,1);
MERGE INTO [metadata].[Pipelines] AS tgt USING @Pipelines AS src ON tgt.[OrchestratorId] = src.[OrchestratorId] AND tgt.[PipelineName] = src.[PipelineName] AND tgt.[StageId] = src.[StageId] WHEN MATCHED THEN UPDATE SET tgt.[LogicalPredecessorId] = src.[LogicalPredecessorId], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [OrchestratorId], [StageId], [PipelineName], [LogicalPredecessorId], [Enabled] ) VALUES ( src.[OrchestratorId], src.[StageId], src.[PipelineName], src.[LogicalPredecessorId], src.[Enabled] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN
	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'OverideRestart',
		@PropertyValue = N'0',
		@Description = N'Should processing not be restarted from the point of failure or should a new execution will be created regardless. 1 = Start New, 0 = Restart. ';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'PipelineStatusCheckDuration',
		@PropertyValue = N'30',
		@Description = N'Duration applied to the Wait activity within the Infant pipeline Until iterations.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'UnknownWorkerResultBlocks',
		@PropertyValue = N'1',
		@Description = N'If a worker pipeline returns an unknown status. Should this block and fail downstream pipeline? 1 = Yes, 0 = No.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'CancelledWorkerResultBlocks',
		@PropertyValue = N'1',
		@Description = N'If a worker pipeline returns an cancelled status. Should this block and fail downstream pipeline? 1 = Yes, 0 = No.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'UseFrameworkEmailAlerting',
		@PropertyValue = N'0',
		@Description = N'Do you want the framework to handle pipeline email alerts via the database metadata? 1 = Yes, 0 = No.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'EmailAlertBodyTemplate',
		@PropertyValue = 
		N'<hr/><strong>Pipeline Name: </strong>##PipelineName###<br/>
	<strong>Status: </strong>##Status###<br/><br/>
	<strong>Execution ID: </strong>##ExecId###<br/>
	<strong>Run ID: </strong>##RunId###<br/><br/>
	<strong>Start Date Time: </strong>##StartDateTime###<br/>
	<strong>End Date Time: </strong>##EndDateTime###<br/>
	<strong>Duration (Minutes): </strong>##Duration###<br/><br/>
	<strong>Called by Orchestrator: </strong>##CalledByOrc###<br/>
	<strong>Executed by Orchestrator Type: </strong>##ExecutedByOrcType###<br/>
	<strong>Executed by Orchestrator: </strong>##ExecutedByOrc###<br/><hr/>',
		@Description = N'Basic HTML template of execution information used as the eventual body in email alerts sent.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'FailureHandling',
		@PropertyValue = N'Simple',
		@Description = N'Accepted values: None, Simple, DependencyChain. Controls processing bahaviour in the event of Worker failures. See v1.8 release notes for full details.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'SPNHandlingMethod',
		@PropertyValue = N'StoreInDatabase',
		@Description = N'Accepted values: StoreInDatabase, StoreInKeyVault. See v1.8.2 release notes for full details.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'ExecutionPrecursorProc',
		@PropertyValue = N'[dbo].[ExampleCustomExecutionPrecursor]',
		@Description = N'This procedure will be called first in the parent pipeline and can be used to perform/update any required custom behaviour in the framework execution. For example, enable/disable Worker pipelines given a certain run time/day. Invalid proc name values will be ignored.'

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'UseExecutionBatches',
		@PropertyValue = N'0',
		@Description = N'Establishes if execution batches are used as a level above execution stages within the framework. 1 = True, 0 = False.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'FrameworkFactoryResourceGroup',
		@PropertyValue = N'ADF.metadata',
		@Description = N'Supports various queries where the framework factory is inspecting itself and the resource group cant be inferred.';

	EXEC [metadataHelpers].[AddProperty]
		@PropertyName = N'PreviousPipelineRunsQueryRange',
		@PropertyValue = N'-1',
		@Description = N'Used as a date range, today +- this value, when checking for if an execution for a given pipeline is already running. Must include +- symbol in value.';
END;

BEGIN
	EXEC [metadataHelpers].[AddRecipientPipelineAlerts]
		@RecipientName = N'Test User 1',
		@AlertForStatus = 'All';

	EXEC [metadataHelpers].[AddRecipientPipelineAlerts]
		@RecipientName = N'Test User 2',
		@PipelineName = 'Intentional Error',
		@AlertForStatus = 'Failed';

	EXEC [metadataHelpers].[AddRecipientPipelineAlerts]
		@RecipientName = N'Test User 3',
		@PipelineName = 'Wait 1',
		@AlertForStatus = 'Success, Failed, Cancelled';	
END;

BEGIN DECLARE @Recipients TABLE ( [Name] [VARCHAR](255) NULL, [EmailAddress] [NVARCHAR](500) NOT NULL, [MessagePreference] [CHAR](3) NOT NULL, [Enabled] [BIT] NOT NULL )
INSERT INTO @Recipients ( [Name], [EmailAddress], [MessagePreference], [Enabled] ) VALUES ('Test User 1','test.user1@adfmetadata.com', 'TO', 1), ('Test User 2','test.user2@adfmetadata.com', 'CC', 1), ('Test User 3','test.user3@adfmetadata.com', 'BCC', 1);
MERGE INTO [metadata].[Recipients] AS tgt USING @Recipients AS src ON tgt.[Name] = src.[Name] WHEN MATCHED THEN UPDATE SET tgt.[EmailAddress] = src.[EmailAddress], tgt.[MessagePreference] = src.[MessagePreference], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [Name], [EmailAddress], [MessagePreference], [Enabled] ) VALUES ( src.[Name], src.[EmailAddress], src.[MessagePreference], src.[Enabled] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN DECLARE @Stages TABLE ( [StageName] [VARCHAR](225) NOT NULL, [StageDescription] [VARCHAR](4000) NULL, [Enabled] [BIT] NOT NULL )
INSERT @Stages ( [StageName], [StageDescription], [Enabled] ) VALUES ('Extract', N'Ingest all data from source systems.', 1), ('Transform', N'Transform ingested data and apply business logic.', 1), ('Load', N'Load transformed data into data warehouse layer.', 1), ('Serve', N'Load transformed data into semantic layer.', 1), ('Speed', N'Regular loading of frequently used data.', 0);
MERGE INTO [metadata].[Stages] AS tgt USING @Stages AS src ON tgt.[StageName] = src.[StageName] WHEN MATCHED THEN UPDATE SET tgt.[StageDescription] = src.[StageDescription], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [StageName], [StageDescription], [Enabled] ) VALUES ( src.[StageName], src.[StageDescription], src.[Enabled] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN DECLARE @Subscriptions TABLE ( [SubscriptionId] UNIQUEIDENTIFIER NOT NULL, [Name] NVARCHAR(200) NOT NULL, [Description] NVARCHAR(MAX) NULL, [TenantId] UNIQUEIDENTIFIER NOT NULL )
INSERT INTO @Subscriptions ( [SubscriptionId], [Name], [Description], [TenantId] ) VALUES ('12345678-1234-1234-1234-012345678910', 'Default', 'Example value for development environment.', '12345678-1234-1234-1234-012345678910');
MERGE INTO [metadata].[Subscriptions] AS tgt USING @Subscriptions AS src ON tgt.[SubscriptionId] = src.[SubscriptionId] WHEN MATCHED THEN UPDATE SET tgt.[Name] = src.[Name], tgt.[Description] = src.[Description], tgt.[TenantId] = src.[TenantId] WHEN NOT MATCHED BY TARGET THEN INSERT ( [SubscriptionId], [Name], [Description], [TenantId] ) VALUES ( src.[SubscriptionId], src.[Name], src.[Description], src.[TenantId] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN DECLARE @Tenants TABLE ( [TenantId] UNIQUEIDENTIFIER NOT NULL, [Name] NVARCHAR(200) NOT NULL, [Description] NVARCHAR(MAX) NULL )
INSERT INTO @Tenants ( [TenantId], [Name], [Description] ) VALUES ('12345678-1234-1234-1234-012345678910', 'Default', 'Example value for development environment.');
MERGE INTO [metadata].[Tenants] AS tgt USING @Tenants AS src ON tgt.[TenantId] = src.[TenantId] WHEN MATCHED THEN UPDATE SET tgt.[Name] = src.[Name], tgt.[Description] = src.[Description] WHEN NOT MATCHED BY TARGET THEN INSERT ( [TenantId], [Name], [Description] ) VALUES ( src.[TenantId], src.[Name], src.[Description] ) WHEN NOT MATCHED BY SOURCE THEN DELETE; END;

BEGIN SET NOCOUNT ON;
--clear default metadata DELETE FROM [metadata].[BatchStageLink];
DELETE FROM [metadata].[Batches];
DELETE FROM [metadata].[PipelineDependencies]; DBCC CHECKIDENT ('[metadata].[PipelineDependencies]', RESEED, 0);
DELETE FROM [metadata].[PipelineAlertLink]; DBCC CHECKIDENT ('[metadata].[PipelineAlertLink]', RESEED, 0);
DELETE FROM [metadata].[Recipients]; DBCC CHECKIDENT ('[metadata].[Recipients]', RESEED, 0);
DELETE FROM [metadata].[PipelineAuthLink]; DBCC CHECKIDENT ('[metadata].[PipelineAuthLink]', RESEED, 0);
DELETE FROM [dbo].[ServicePrincipals]; DBCC CHECKIDENT ('[dbo].[ServicePrincipals]', RESEED, 0);
DELETE FROM [metadata].[PipelineParameters]; DBCC CHECKIDENT ('[metadata].[PipelineParameters]', RESEED, 0);
DELETE FROM [metadata].[Pipelines]; DBCC CHECKIDENT ('[metadata].[Pipelines]', RESEED, 0);
--get Orchestrator id DECLARE @OrcId INT
SELECT @OrcId = [OrchestratorId] FROM [metadata].[Orchestrators] WHERE [OrchestratorName] = 'WorkersFactory' AND [OrchestratorType] = 'ADF';
--tweak properties UPDATE [metadata].[Properties] SET [PropertyValue] = '[dbo].[None]' WHERE [PropertyName] = 'ExecutionPrecursorProc';
UPDATE [metadata].[Properties] SET [PropertyValue] = '30' WHERE [PropertyName] = 'PipelineStatusCheckDuration';
UPDATE [metadata].[Properties] SET [PropertyValue] = '0' WHERE [PropertyName] = 'UseFrameworkEmailAlerting';
UPDATE [metadata].[Properties] SET [PropertyValue] = '1' WHERE [PropertyName] = 'UseExecutionBatches';
--insert 200 pipelines ;WITH cte AS ( SELECT TOP 200 ROW_NUMBER() OVER (ORDER BY s1.[object_id]) AS Number FROM sys.all_columns AS s1 CROSS JOIN sys.all_columns AS s2 ) INSERT INTO [metadata].[Pipelines] ( [OrchestratorId], [StageId], [PipelineName], [LogicalPredecessorId], [Enabled] ) SELECT @OrcId, CASE WHEN [Number] <= 50 THEN 1 WHEN [Number] > 50 AND [Number] <= 100 THEN 2 WHEN [Number] > 100 AND [Number] <= 150 THEN 3 WHEN [Number] > 150 AND [Number] <= 200 THEN 4 END, 'Wait ' + CAST([Number] AS VARCHAR), NULL, 1 FROM cte;
--disable other execution stages if exist UPDATE [metadata].[Stages] SET [Enabled] = 1; UPDATE [metadata].[Stages] SET [Enabled] = 0 WHERE [StageId] > 4;
--insert 200 pipeline parameters INSERT INTO [metadata].[PipelineParameters] ( [PipelineId], [ParameterName], [ParameterValue] ) SELECT [PipelineId], 'WaitTime', LEFT(ABS(CAST(CAST(NEWID() AS VARBINARY) AS INT)),1) FROM [metadata].[Pipelines];
--insert batches ;WITH batchNames AS ( SELECT 'One' AS [Name] UNION SELECT 'Two' UNION SELECT 'Three' UNION SELECT 'Four' UNION SELECT 'Five' UNION SELECT 'Six' UNION SELECT 'Seven' UNION SELECT 'Eight' UNION SELECT 'Nine' UNION SELECT 'Ten' UNION SELECT 'Eleven' UNION SELECT 'Twelve' UNION SELECT 'Thirteen' UNION SELECT 'Fourteen' UNION SELECT 'Fifteen' UNION SELECT 'Sixteen' UNION SELECT 'Seventeen' UNION SELECT 'Eighteen' UNION SELECT 'Nineteen' UNION SELECT 'Twenty' ) INSERT INTO [metadata].[Batches] ( [BatchName], [BatchDescription], [Enabled] ) SELECT [Name], '1000 Workers Test', 1 FROM batchNames
--allocation batches to stages evenly ;WITH maxStages AS ( SELECT MAX([StageId]) AS Id FROM [metadata].[Stages] WHERE [Enabled] = 1 ) INSERT INTO [metadata].[BatchStageLink] SELECT b.[BatchId], CASE WHEN (ROW_NUMBER() OVER (ORDER BY b.[BatchName] DESC) * 1) % maxStages.[Id] = 0 THEN maxStages.[Id] ELSE (ROW_NUMBER() OVER (ORDER BY b.[BatchName] DESC) * 1) % maxStages.[Id] END AS Stage FROM [metadata].[Batches] b CROSS JOIN maxStages;
/* --generate the c# for the NUnit test:
SELECT [BatchName], 'private GrandparentHelper _helperBatch' + [BatchName] + ';', '_helperBatch' + [BatchName] + ' = new GrandparentHelper().WithParameter("BatchName", "' + [BatchName] + '");', 'var batch' + [BatchName] + ' = _helperBatch' + [BatchName] + '.RunPipeline();', 'batch' + [BatchName] + ',', '[Test]' + CHAR(13) + 'public void Then' + [BatchName] + 'BatchPipelineOutcomeIsSucceeded()' + CHAR(13) + '{' + CHAR(13) + ' _helperBatch' + [BatchName] + '.RunOutcome.Should().Be("Succeeded");' + CHAR(13) + '}' FROM [metadata].[Batches] */ END;

BEGIN SET NOCOUNT ON;
--clear default metadata DELETE FROM [metadata].[BatchStageLink]; DELETE FROM [metadata].[Batches];
--add batch details ;WITH sourceData AS ( SELECT '0to300' AS BatchName, 'The first 300.' AS BatchDescription, 1 AS [Enabled] UNION SELECT '301to600', 'The second 300.', 1 ) MERGE INTO [metadata].[Batches] AS tgt USING sourceData AS src ON tgt.[BatchName] = src.[BatchName] WHEN MATCHED THEN UPDATE SET tgt.[BatchDescription] = src.[BatchDescription], tgt.[Enabled] = src.[Enabled] WHEN NOT MATCHED BY TARGET THEN INSERT ( [BatchName], [BatchDescription], [Enabled] ) VALUES ( src.[BatchName], src.[BatchDescription], src.[Enabled] ) WHEN NOT MATCHED BY SOURCE THEN DELETE;
--link batches to stages INSERT INTO [metadata].[BatchStageLink] ( [BatchId], [StageId] ) SELECT b.[BatchId], s.[StageId] FROM [metadata].[Batches] b INNER JOIN [metadata].[Stages] s ON s.[Enabled] = 1; END;

BEGIN
	SET NOCOUNT ON;

	--clear default metadata
	DELETE FROM [metadata].[PipelineDependencies];
	DBCC CHECKIDENT ('[metadata].[PipelineDependencies]', RESEED, 0);

	DELETE FROM [metadata].[PipelineAlertLink];
	DBCC CHECKIDENT ('[metadata].[PipelineAlertLink]', RESEED, 0);

	DELETE FROM [metadata].[Recipients];
	DBCC CHECKIDENT ('[metadata].[Recipients]', RESEED, 0);

	DELETE FROM [metadata].[PipelineAuthLink];
	DBCC CHECKIDENT ('[metadata].[PipelineAuthLink]', RESEED, 0);

	DELETE FROM [dbo].[ServicePrincipals];
	DBCC CHECKIDENT ('[dbo].[ServicePrincipals]', RESEED, 0);

	DELETE FROM [metadata].[PipelineParameters];
	DBCC CHECKIDENT ('[metadata].[PipelineParameters]', RESEED, 0);

	DELETE FROM [metadata].[Pipelines];
	DBCC CHECKIDENT ('[metadata].[Pipelines]', RESEED, 0);

	--get Orchestrator id
	DECLARE @OrcId INT

	SELECT 
		@OrcId = [OrchestratorId] 
	FROM 
		[metadata].[Orchestrators]
	WHERE 
		[OrchestratorName] = 'WorkersFactory'
		AND [OrchestratorType] = 'ADF';

	--insert 300 pipelines
	;WITH cte AS
		(
		SELECT TOP 300
			ROW_NUMBER() OVER (ORDER BY s1.[object_id]) AS Number
		FROM 
			sys.all_columns AS s1
			CROSS JOIN sys.all_columns AS s2
		)
	INSERT INTO [metadata].[Pipelines]
		(
		[OrchestratorId],
		[StageId],
		[PipelineName],
		[LogicalPredecessorId],
		[Enabled]
		)
	SELECT
		@OrcId,
		CASE
			WHEN [Number] <= 100 THEN 1
			WHEN [Number] > 100 AND  [Number] <= 200 THEN 2
			WHEN [Number] > 200 AND  [Number] <= 300 THEN 3
		END,
		'Wait ' + CAST([Number] AS VARCHAR),
		NULL,
		1
	FROM
		cte;

	--disable other execution stages if exist
	UPDATE [metadata].[Stages] SET [Enabled] = 0 WHERE [StageId] > 3;

	--insert 300 pipeline parameters
	INSERT INTO [metadata].[PipelineParameters]
		(
		[PipelineId],
		[ParameterName],
		[ParameterValue]
		)
	SELECT
		[PipelineId],
		'WaitTime',
		LEFT(ABS(CAST(CAST(NEWID() AS VARBINARY) AS INT)),2)
	FROM
		[metadata].[Pipelines];
END;

BEGIN IF @PipelineName IS NULL BEGIN WHILE 1=1 BEGIN IF EXISTS ( SELECT TOP 1 [PipelineRunId] FROM [metadata].[CurrentExecution] WHERE [PipelineRunId] IS NOT NULL ) BEGIN BREAK; END
WAITFOR DELAY '00:00:10'; END;
SELECT TOP 1 CAST([PipelineRunId] AS VARCHAR(36)) AS RunId FROM [metadata].[CurrentExecution] WHERE [PipelineRunId] IS NOT NULL END ELSE IF @PipelineName IS NOT NULL BEGIN WHILE 1=1 BEGIN IF EXISTS ( SELECT TOP 1 [PipelineRunId] FROM [metadata].[CurrentExecution] WHERE [PipelineRunId] IS NOT NULL AND [PipelineName] = @PipelineName ) BEGIN BREAK; END
WAITFOR DELAY '00:00:10'; END;
SELECT TOP 1 CAST([PipelineRunId] AS VARCHAR(36)) AS RunId FROM [metadata].[CurrentExecution] WHERE [PipelineRunId] IS NOT NULL AND [PipelineName] = @PipelineName END ELSE BEGIN RAISERROR('Unknown use of testing procedure.',16,1); END END;

BEGIN	
	EXEC [metadataHelpers].[SetDefaultProperties];
	EXEC [metadataHelpers].[SetDefaultTenant];
	EXEC [metadataHelpers].[SetDefaultSubscription];
	EXEC [metadataHelpers].[SetDefaultOrchestrators];
	EXEC [metadataHelpers].[SetDefaultBatches];
	EXEC [metadataHelpers].[SetDefaultStages];
	EXEC [metadataHelpers].[SetDefaultBatchStageLink];
	EXEC [metadataHelpers].[SetDefaultPipelines];
	EXEC [metadataHelpers].[SetDefaultPipelineParameters];
	EXEC [metadataHelpers].[SetDefaultPipelineDependants];
	EXEC [metadataHelpers].[SetDefaultRecipients];
	EXEC [metadataHelpers].[SetDefaultAlertOutcomes];
	EXEC [metadataHelpers].[SetDefaultRecipientPipelineAlerts];
END;
